INSERT INTO product_categories (id, name, slug, description, display_order, is_active) VALUES
  ('a1b2c3d4-e5f6-7890-abcd-ef1234567890', 'Industrial Pumps', 'industrial-pumps', 'High-performance pumping solutions for demanding industrial applications. Our range includes centrifugal, positive displacement, and specialty pumps designed for reliability and efficiency.', 1, true),
  ('b2c3d4e5-f6a7-8901-bcde-f12345678901', 'Compressors', 'compressors', 'Industrial-grade air and gas compressors engineered for continuous operation. From rotary screw to reciprocating models, built for maximum uptime.', 2, true),
  ('c3d4e5f6-a7b8-9012-cdef-123456789012', 'Conveyor Systems', 'conveyor-systems', 'Complete conveyor solutions for material transport and handling. Belt, roller, and modular systems designed for your specific workflow requirements.', 3, true),
  ('d4e5f6a7-b8c9-0123-def0-234567890123', 'Industrial Valves', 'industrial-valves', 'Precision valve solutions for flow control in critical applications. Gate, ball, butterfly, and check valves meeting international standards.', 4, true),
  ('e5f6a7b8-c9d0-1234-ef01-345678901234', 'Power Generation', 'power-generation', 'Reliable power generation equipment including diesel generators, turbines, and backup power systems for industrial facilities.', 5, true),
  ('f6a7b8c9-d0e1-2345-f012-456789012345', 'Material Handling', 'material-handling', 'Complete material handling solutions including hoists, cranes, forklifts, and automated systems for efficient operations.', 6, true)
